(40, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2022-04-21 11:32:16', NULL, 0, 'plain', '', 0, 0, 'info'),
(41, 'pinterest_gtm_q2_2021', 'marketing', 'en_US', 'Get your products in front of a large, engaged audience', 'Did you know that shoppers on Pinterest spend twice as much as people on other social platforms? With the new, official Pinterest for WooCommerce, you can turn your product catalog into shoppable Pins and showcase them to 400+ million monthly users looking to buy.', '{}', 'unactioned', 'woocommerce.com', '2022-04-25 21:49:49', NULL, 0, 'plain', '', 0, 0, 'info'),
