(43, 'woocommerce_payments_subscriptions_nopayments_q2_2022', 'marketing', 'en_US', 'Sell subscriptions online  without a paid extension', 'Say hello to WooCommerce Payments  now with built-in subscriptions functionality. Get free access to the features you need to start earning recurring revenue online. Pay as you go, with no setup costs or monthly fees.', '{}', 'unactioned', 'woocommerce.com', '2022-04-26 20:22:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(44, 'woocommerce_payments_subscriptions_yespayments_q2_2022', 'marketing', 'en_US', 'Sell subscriptions online  no extra extension required', 'It\'s here! WooCommerce Payments now features built-in subscriptions functionality. No need to install an extra, paid extension  we\'ve made it simpler than ever to start earning recurring revenue. Ready to grow your business?', '{}', 'pending', 'woocommerce.com', '2022-04-26 20:22:11', NULL, 0, 'plain', '', 0, 0, 'info'),
